package _6;

import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.atomic.AtomicInteger;

public class Task2 {

    public static final int OFFSET = 4;

    public static void main(String[] args) {
        String input = "";
        Map<Character, AtomicInteger> charMap = new HashMap<>();
        for (int i = 0; i < input.length(); i++) {

            char c = input.charAt(i);

            AtomicInteger atomicInteger = charMap.computeIfAbsent(c, d -> new AtomicInteger());
            atomicInteger.incrementAndGet();

            if (i >= OFFSET) {
                AtomicInteger atomicInteger1 = charMap.get(input.charAt(i - OFFSET));

                atomicInteger1.decrementAndGet();
                if (atomicInteger1.get() == 0) {
                    charMap.remove(input.charAt(i - OFFSET));
                }
            }

            if (charMap.size() == OFFSET) {
                System.out.println(i + 1);
                return;
            }
        }
    }
}
